mapper.update:
update
    t_user_infomation
set
    user_pass = #{cinPass}
where
    user_code = #{userCode}
and
    is_delete = 0
and
    user_pass = #{userPass}

update
    t_driver_information_manage
set
    driver_pass = #{cinPass}
where
    driver_code = #{userCode}
and
    is_delete = 0
and
    driver_pass = #{userPass}
