mapper.select:
select torder.order_code orderCode, torder.order_price orderCountPrice, torder.order_state orderState, torder.order_play_state orderPayState, tbind.shop_code storeCode, tuser.user_name orderUserName, tuser.user_phone orderUserPhone, tuser.order_sure_pay_time orderPayTime from t_order_manage torder, t_store_bind tbind, t_user_infomation tuser, t_store_information tstore where torder.user_code = tuser.user_code and tbind.user_code = tuser.user_code and tstore.shop_code = tbind.shop_code
and tuser.user_name like concat('%',#{orderUserName},'%')
and torder.order_code like concat('%',#{orderCode},'%')
and tuser.user_phone like concat('%',#{orderUserPhone},'%')
and torder.order_state = CAST(#{orderState} AS SIGNED)
and torder.order_sure_pay_time >= #{orderPayTimeStart}
and torder.order_sure_pay_time <= #{orderPayTimeEnd}
and tstore.shop_con_user = #{userCode}
